import "isomorphic-unfetch"

class ApiClient {
    get(url, paramaters) {
        return fetch(this.url(url), paramaters)
                .map(c => c.json())
    }

    url(endpoint) {
        return `http://localhost:3000/api/${endpoint}`
    }
}

export default new ApiClient()
