import * as Yup from 'yup';
import useLocale from '../hooks/locale';
const {
  paymentMethodForm: {
    form
  }
} = useLocale();
const PaymentMethodSchema = Yup.object().shape({
  name: Yup.string().min(2, form.name.error).max(50, form.name.error).required(form.name.error),
  career: Yup.number().required(form.email.error),
  birthdate: Yup.date().required(form.name.error),
  country: Yup.number().required('Required'),
  city: Yup.string().required('Required'),
  phone: Yup.string().required('Required'),
  email: Yup.string().email('Invalid email').required('Required')
});
export { PaymentMethodSchema };
