import { createResponse, createErrorResponse } from "../../../utils/httpBuilderResponse"
import {getManager, Connection} from "typeorm"
import { Country } from "../../../database/entity/Country"
import connection from '../../../connection'


export default async (req, res) => {
    try {        
        const c: Connection = connection
        const countries = await c.getRepository(Country);
        createResponse(res, {
            countries,
        })
    } catch(err) {
        console.log('err', err)
        createErrorResponse(res)
    }

}
