import { createResponse, createErrorResponse } from "../../../utils/httpBuilderResponse"
import {getManager} from "typeorm"
import { Country } from "../../../database/entity/Country"


export default async (req, res) => {
    try {        
        const entityManager = getManager()
        const rep = await entityManager.getRepository(Country)
        console.log(rep)
        const countries = await entityManager.find(Country);
        createResponse(res, {
            countries,
        })
    } catch(err) {
        console.log('err', err)
        createErrorResponse(res)
    }

}
