import { createResponse, createErrorResponse } from "../../../utils/httpBuilderResponse"
import {getManager, Connection} from "typeorm"
import { Country } from "../../../database/entity/Country"
import getConnection from '../../../connection'


export default async (req, res) => {
    try {        
        const connection = getConnection()
        console.log(connection)
        const countries = await connection.getRepository(Country);
        console.log(countries)
        createResponse(res, {
            countries,
        })
    } catch(err) {
        console.log('err', err)
        createErrorResponse(res)
    }

}
