import 'date-fns'

import {
    Formik,
    Form,
    Field,
  } from 'formik'

import FormControl from '@material-ui/core/FormControl'
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem'
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';


import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers'
import DateFnsUtils from '@date-io/date-fns'
import useLocale from '../../hooks/locale'

function PaymentMethodForm({ countries, careers }) {
    const initialValues = { firstName: '' }
    const { paymentMethodForm: { form } } = useLocale()
    const date = new Date()
    const l = form

    return (
        <React.Fragment>
          <MuiPickersUtilsProvider utils={DateFnsUtils}>

              <Formik
                initialValues={initialValues}
                onSubmit={(values, actions) => {
                  console.log({ values, actions });
                  alert(JSON.stringify(values, null, 2));
                  actions.setSubmitting(false);
                }}
                render={formikBag => (
                  <Form>
                  <Grid container spacing={3}>

                    <Grid item xs={6}>
                      <Field
                        name="name"
                        render={({ field, form, meta }) => (
                          <FormControl fullWidth>
                            <TextField label={l.name.label} id="name" {...field} aria-describedby="name" />
                            {meta.touched && meta.error && meta.error}
                          </FormControl>
                        )}
                      />
                    </Grid>

                    <Grid item xs={6}>
                      <Field
                        name="email"
                        render={({ field, form, meta }) => (
                          <FormControl fullWidth>
                            <TextField label={l.email.label} id={l.email.label} {...field} aria-describedby={l.email.label} />
                            {meta.touched && meta.error && meta.error}
                            </FormControl>
                        )}
                      />
                    </Grid>
                    <Field
                      name="career"
                      render={({ field, form, meta }) => (
                        <FormControl fullWidth>
                          <TextField
                          label={l.career.label}
                          id="career"
                          select
                          >
                          {careers.map(option => (
                            <MenuItem key={option.id} value={option.name}>
                              {option.name}
                            </MenuItem>
                          ))}
                          </TextField>
                          {meta.touched && meta.error && meta.error}
                          </FormControl>
                      )}
                    />
                    <Field
                      name="birdate"
                      render={({ field, form, meta }) => (
                        <FormControl fullWidth>
                        <KeyboardDatePicker
                          disableToolbar
                          variant="inline"
                          format="MM/dd/yyyy"
                          margin="normal"
                          id="birthdate"
                          label={l.birthdate.label}
                          KeyboardButtonProps={{
                            'aria-label': 'change date',
                          }}
                        />
                          {meta.touched && meta.error && meta.error}
                          </FormControl>
                      )}
                    />
                    <Field
                      name="phone"
                      render={({ field, form, meta }) => (
                        <FormControl fullWidth>
                          <TextField label={l.phone.label} id={l.email.label} {...field} aria-describedby={l.email.label} />
                          {meta.touched && meta.error && meta.error}
                          </FormControl>
                      )}
                    />
                    <Field
                      name="country"
                      render={({ field, form, meta }) => (
                        <FormControl fullWidth>
                          <TextField
                          label="PaÃ­s"
                          id="standard-select-currency"
                          select
                          >
                          {countries.map(option => (
                            <MenuItem key={option.id} value={option.name}>
                              {option.name}
                            </MenuItem>
                          ))}
                          </TextField>
                          {meta.touched && meta.error && meta.error}
                          </FormControl>
                      )}
                    />
                    <Field
                      name="phone"
                      render={({ field, form, meta }) => (
                        <FormControl fullWidth>
                          <TextField label={l.city.label} id="city" {...field} aria-describedby={l.city.label} />
                          {meta.touched && meta.error && meta.error}
                          </FormControl>
                      )}
                    />

                    <Button variant="contained" color="primary" href="#contained-buttons">
                      Link
                    </Button>
                    </Grid>
                  </Form>
                )}
              />

          </MuiPickersUtilsProvider>
        </React.Fragment>
      );
}

export default PaymentMethodForm
