import 'date-fns'

import {
    Formik,
    Form,
    Field,
  } from 'formik'

import FormControl from '@material-ui/core/FormControl'
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem'
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers'
import DateFnsUtils from '@date-io/date-fns'

import useLocale from '../../hooks/locale'

function PaymentMethodForm({ countries }) {
    const initialValues = { firstName: '' }
    const { paymentMethodForm: { form } } = useLocale()

    const carrers = [
      {
        value: 1,
        label: 'Desarrollo web Full Stack',
      },
      {
        value: 2,
        label: 'Diseño UX/UI',
      },
      {
        value: 3,
        label: 'Data Science',
      },
      {
        value: 4,
        label: 'Front-end Avanzado en React',
      },
    ]

    const date = new Date('2014-08-18T21:11:54')

    return (
        <React.Fragment>
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
          <Formik
            initialValues={initialValues}
            onSubmit={(values, actions) => {
              console.log({ values, actions });
              alert(JSON.stringify(values, null, 2));
              actions.setSubmitting(false);
            }}
            render={formikBag => (
              <Form>
                <Field
                  name="name"
                  render={({ field, form, meta }) => (
                    <FormControl >
                      <TextField label="Nombre" id="name" {...field} aria-describedby="name" />
                      {meta.touched && meta.error && meta.error}
                    </FormControl>
                  )}
                />
                <Field
                  name="email"
                  render={({ field, form, meta }) => (
                    <FormControl >
                      <TextField label="Correo electrónico" id="email" {...field} aria-describedby="email address" />
                      {meta.touched && meta.error && meta.error}
                      </FormControl>
                  )}
                />
                <Field
                  name="career"
                  render={({ field, form, meta }) => (
                    <FormControl fullWidth>
                      <TextField
                      label="Carrera"
                      id="standard-select-currency"
                      select
                      >
                      {carrers.map(option => (
                        <MenuItem key={option.value} value={option.value}>
                          {option.label}
                        </MenuItem>
                      ))}
                      </TextField>
                      {meta.touched && meta.error && meta.error}
                      </FormControl>
                  )}
                />
                <Field
                  name="birdate"
                  render={({ field, form, meta }) => (
                    <FormControl >
                    <KeyboardDatePicker
                      disableToolbar
                      variant="inline"
                      format="MM/dd/yyyy"
                      margin="normal"
                      id="date-picker-inline"
                      label="Date picker inline"
                      value={date}
                      KeyboardButtonProps={{
                        'aria-label': 'change date',
                      }}
                    />
                      {meta.touched && meta.error && meta.error}
                      </FormControl>
                  )}
                />
                <Field
                  name="country"
                  render={({ field, form, meta }) => (
                    <FormControl fullWidth>
                      <TextField
                      label="Carrera"
                      id="standard-select-currency"
                      select
                      >
                      {countries.map(option => (
                        <MenuItem key={option.value} value={option.value}>
                          {option.label}
                        </MenuItem>
                      ))}
                      </TextField>
                      {meta.touched && meta.error && meta.error}
                      </FormControl>
                  )}
                />
              </Form>
            )}
          />
          </MuiPickersUtilsProvider>
        </React.Fragment>
      );
}

export default PaymentMethodForm
